sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var whatneeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');

	if(newValue =='hr')
		{
			g_form.addOption('u_what_needed','hr1','Human Resoucrces 1');
			g_form.addOption('u_what_needed','hr2','Human Resoucrces 2');
			g_form.addOption('u_what_needed','hr3','Human Resoucrces 3');
		}
	if(newValue =='facilities')
		{
			g_form.addOption('u_what_needed','fc1','Facilities 1');
			g_form.addOption('u_what_needed','fc2','Facilities 2');
			g_form.addOption('u_what_needed','fc3','Facilities 3');
		}
	if(newValue =='legal')
		{
			g_form.addOption('u_what_needed','le1','Legal  1');
			g_form.addOption('u_what_needed','le2','Legal  2');
			g_form.addOption('u_what_needed','le3','Legal  3');
		}
   if(isLoading && !g_form.isNewRecord())
		{
			g_form.setValue('u_what_needed',whatneeded);

		}
}
